sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    //alert('hello');
    if (isLoading || newValue == '') {
        //alert('hello2');
        return;
    }

    //Type appropriate comment here, and begin script below
    //alert('going to call SInclude');
    var ga = new GlideAjax('ISLLmsAjax');
    ga.addParam('sysparm_name', 'getLeaveBucket');
    ga.addParam('sysparm_user', g_form.getValue('requested_by'));
    ga.addParam('sysparm_leavetype', newValue.toString());
    //alert(newValue.toString());
    ga.getXML(getBucket);

    function getBucket(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        var result = JSON.parse(answer);
        //alert(result.accrued);
        g_form.setValue('accrued', result.accrued);
        g_form.setValue('balance', result.balance);
        g_form.setValue('taken', result.taken);
    }

}
