sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {

        control.style = 'background-color:';
        return;
    }

    switch (newValue) {
        case "approved":
            control.style = 'background-color: limegreen';
            break;
        case "nyr":
            control.style = 'background-color: lightblue';
            break;
        case "rejected":
            control.style = 'background-color: red';
            break;
        case "requested":
            control.style = 'background-color: orange';
            break;
    }

    //Type appropriate comment here, and begin script below

}
